var connection = require("Conexion");

module.exports = CambiarCarta;

//Cambia la carta de una mano de un jugador, ya sea por descartar una carta o por haberla puesto en el tablero
function CambiarCarta(partida, usuario, carta, callback){
    var conexion = connection.getConexion();
    if (callback === undefined) {
        callback = function () {};
    }

    conexion.connect(function (err) {
        if (!err) {
           var sql = "Select partidas.ID as game, usuario.ID as user " +
                     "from partidas, usuario " +
                     "where partidas.Nombre = ? and usuario.Nombre_Usuario = ?;";
             
            conexion.query(sql, [partida, usuario], function (err, resultado){
                if (!err) {
                    sql = "update tiene " +
                            "set ID_Carta = ? " +
                            "where ID_Carta = ? and ID_Usuario = ? and ID_Partida = ? " + 
                            "LIMIT 1;";
                    conexion.query(sql, [Math.floor((Math.random() * 15) + 5), carta, resultado[0].user, resultado[0].game], function (err, resultado) {
                        if (!err) {
                            callback(null, resultado);
                        } else {
                            callback(err);
                        }
                        conexion.end();
                    });
                } else {
                    callback(err);
                }
            });
        } else {
            callback(err);
        }
    });
}