var connection = require("Conexion");

module.exports = LeerDatosCarta;

//Esta funcion lee el tipo y la url de la foto de la carta mediante el uso de un ID de carta
function LeerDatosCarta(ID, callback) {
    var conexion = connection.getConexion();
    if (callback === undefined) {
        callback = function () {};
    }

    conexion.connect(function (err) {
        if (!err) {
            var sql = "select cartas.Tipo, cartas.URL " +
                    "from cartas " +
                    "where cartas.ID = ?;";
            conexion.query(sql, [ID], function (err, resultado) {
                if (!err) {
                    callback(null, resultado);
                } else {
                    callback(err);
                }
                conexion.end();
            });
        } else {
            callback(err);
        }
    });
}