var connection = require("Conexion");
var game = require("../Partida/LeerPartidas.js");

module.exports = CambiarTurno;

function CambiarTurno(partida, callback){
    var conexion = connection.getConexion();
    if (callback === undefined) {
        callback = function () {};
    }

    conexion.connect(function (err) {
        if (!err) {
            game.PartidaActual(partida, function (err, PartidaActual) {
                if (!err) {
                    var busqueda = PartidaActual[0].Turno;
                    var indice = -1;
                    for (var i = 0, len = PartidaActual.length; i < len; i++) {
                        if (PartidaActual[i].Nombre_Usuario === busqueda) {
                            indice = i;
                            break;
                        }
                    }
                    if (indice === len - 1) {
                        indice = 0;
                    } else {
                        indice++;
                    }
                    
                    var sql = "update partidas " +
                            "set Turno = '" + PartidaActual[indice].Nombre_Usuario + "', Turnos_restantes = '" + (PartidaActual[0].Turnos_restantes - 1) + "' " +
                            "where partidas.Nombre = '" + partida + "';";
                    conexion.query(sql, function (err,resultado) {
                        if (!err) {
                            callback(null, (PartidaActual[0].Turnos_restantes - 1));
                        } else {
                            callback(err);
                        }
                    });
                } else {
                    callback(err);
                }
            });
        } else {
            callback(err);
        }
    });
}