var connection = require("Conexion");

module.exports = leerTablero;

function leerTablero(partida){
    var conexion = connection.getConexion();
    if (callback === undefined) {
        callback = function () {};
    }

    conexion.connect(function (err) {
        if (!err) {
            var sql = "select Fila, Columna, ID_Carta " + 
                  "from poner " + 
                  "where ID_Partida = '" + partida + "' " + 
                  "order by Fila, asc Columna asc";
            conexion.query(sql, function (err, filas){
                if(!err){
                    callback(null, filas);
                } else{
                    callback(err);
                }
                conexion.end();
            });
        } else {
            callback(err);
        }
        
    });
}