var connection = require("Conexion");

module.exports = PonerFichaTablero;

function PonerFichaTablero(usuario, partida, carta, fila, columna, callback){
    var conexion = connection.getConexion();
    if (callback === undefined) {
        callback = function () {};
    }

    conexion.connect(function (err) {

        if (!err) {
            var sql = "insert into poner " + 
                      "(`ID_Usuario`, `ID_Partida`, `ID_Carta`, `Fila`, `Columna`) " +
                      "VALUES ('" + usuario + "', '" + partida + "', '" + carta + "', '" + fila + "', '" + columna + "';";
            conexion.query(sql, function (err, resultado){
                if(!err){
                    callback(resultado);
                } else{
                    callback(err);
                }
            });
        } else {
            callback(err);
        }
    });
}