var connection = require("Conexion");

module.exports = LeerUsuario;

function LeerUsuario(usuario, callback){
    
    var conexion = connection.getConexion();
    if(callback === undefined){
        callback = function(){};
    }
        conexion.connect(function (err) {
        if (err) {
            callback(err);
        } else {
            var sql = "select Nombre_Completo, Sexo, Foto " +
                    "from usuario " +
                    "where usuario.Nombre_Usuario = '" + usuario + "'";
            conexion.query(sql, function (err, filas) {
                if (err) {
                    callback(err);
                } else {
                   callback(null, filas);
                }
                conexion.end();
            });
        }
    });
}